// Funciones en JS

function saludar(nombre) {
    return `Hola, ${nombre}`;
}

const saludar2 = (nombre) => {
    return `Hola, ${nombre}`;
}

const saludar3 = (nombre) => `Hola, $ (nombre)`;


console.log(saludar('Vegeta'))
console.log(saludar2('Goku'))
console.log(saludar3('Sandia'))
