//Desestructuración

const persona = {
    nombre: 'Sandia',
    edad: 0,
    clave: 'Gordito'
};

const {edad, clave, nombre } = persona;

// console.log (persona.nombre)
// console.log(edad);

const useContext = ({clave, nombre, edad }) => {
    return {
        nombreClave: clave,
        anios: edad
    }
}

const {nombreClave, anios} = useContext(persona);
console.log(nombreClave,anios);
