// Desestructuracion o asignacion desestructurante

const persona = {
    nombreclave: 'Sandia',
    edad: 0,
    clave: 'gordito'
};

// console.log(persona.nombre)
// console.log(persona.edad)
// console.log(persona.clave)

// const {nombre, edad, clave} = persona; //extrae lo que pongo dentro de la llave del objeto

// console.log(nombre)
// console.log(edad)
// console.log(clave)

// const retornaPersona = ({nombre, edad, clave}) => {
//     console.log(edad, clave, nombre);
// }

// retornaPersona(persona);

const useContext = ({nombre, edad, clave}) => {
    return {
        nombreClave: clave,
        anios: edad
    }
}

const {nombreClave, anios} = useContext (persona);
console.log(nombreClave, anios)
