// Variables y Constantes

const nombre = 'Vanessa';
const apellido = 'Milesi';

let valorDado = 5; //variable que puede cambiar de valor

console.log( nombre, apellido, valorDado )

if (true) {
    let valorDado = 6;
    console.log(valorDado)
}

console.log(valorDado); // variables de scope , tiene el mismo valor asignado anterior.
